import React, { useState } from 'react';
import 'react-datepicker/dist/react-datepicker.css';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [startYear, setStartYear] = useState('');
  const [endYear, setEndYear] = useState('');
  const [director, setDirector] = useState('');
  const [sortOption, setSortOption] = useState('latestUpdate');
  const [results, setResults] = useState([]);

  const handleSearch = (e) => {
    e.preventDefault();
    // KOBIS API 호출 로직 추가
    // 예제 데이터로 대체
    let fetchedResults = [
      {
        titleKorean: '영화 1',
        titleEnglish: 'Movie 1',
        productionYear: '2022',
        productionCountry: 'Korea',
        type: 'Feature',
        genre: 'Drama',
        productionStatus: 'Released',
        director: 'Director 1',
        productionCompany: 'Company 1'
      },
      {
        titleKorean: '영화 2',
        titleEnglish: 'Movie 2',
        productionYear: '2021',
        productionCountry: 'Korea',
        type: 'Feature',
        genre: 'Action',
        productionStatus: 'Released',
        director: 'Director 2',
        productionCompany: 'Company 2'
      },
      {
        titleKorean: '영화 3',
        titleEnglish: 'Movie 3',
        productionYear: '2022',
        productionCountry: 'Korea',
        type: 'Feature',
        genre: 'Comedy',
        productionStatus: 'Post-production',
        director: 'Director 3',
        productionCompany: 'Company 3'
      }
    ];

    // 정렬 로직
    if (sortOption === 'latestUpdate') {
      fetchedResults.sort((a, b) => b.productionYear - a.productionYear);
    } else if (sortOption === 'productionYear') {
      fetchedResults.sort((a, b) => b.productionYear - a.productionYear);
    } else if (sortOption === 'title') {
      fetchedResults.sort((a, b) => a.titleKorean.localeCompare(b.titleKorean));
    } else if (sortOption === 'releaseDate') {
      fetchedResults.sort((a, b) => new Date(a.releaseDate) - new Date(b.releaseDate));
    }

    setResults(fetchedResults);
  };

  return (
      <div className="App">
        <header className="App-header">
          <h1>KOBIS 영화 검색</h1>
          <form onSubmit={handleSearch}>
            <div className="form-row">

                <div style={{ display: 'flex' }}>
                  <h3>영화명</h3>
                  <input
                      type="text"
                      value={query}
                      onChange={(e) => setQuery(e.target.value)}
                      placeholder="영화 제목을 입력하세요"
                  />

                  <h3>감독명</h3>
                  <input
                      type="text"
                      value={director}
                      onChange={(e) => setDirector(e.target.value)}
                      placeholder="감독 이름을 입력하세요"
                  />
                </div>
            </div>

              <div className="form-row" style={{ display: 'flex' }}>
                <h3>제작 연도</h3>
                <input
                    type="number"
                    value={startYear}
                    onChange={(e) => setStartYear(e.target.value)}
                    placeholder="시작 연도"
                />
                <input
                    type="number"
                    value={endYear}
                    onChange={(e) => setEndYear(e.target.value)}
                    placeholder="종료 연도"
                />

                <h3>정렬 기준</h3>
                <select value={sortOption} onChange={(e) => setSortOption(e.target.value)}>
                  <option value="latestUpdate">최근 업데이트 순</option>
                  <option value="productionYear">제작 연도</option>
                  <option value="title">영화명</option>
                  <option value="releaseDate">개봉일 순</option>
                </select>
              </div>


            <button type="submit">검색</button>
            <button type="reset">초기화</button>
          </form>

          <div className="results">
            {results.length > 0 ? (
                <table>
                  <thead>
                  <tr>
                    <th>영화명 (한국어)</th>
                    <th>영화명 (영어)</th>
                    <th>제작 연도</th>
                    <th>제작 국가</th>
                    <th>유형</th>
                    <th>장르</th>
                    <th>제작 상태</th>
                    <th>감독</th>
                    <th>제작사</th>
                  </tr>
                  </thead>
                  <tbody>
                  {results.map((movie, index) => (
                      <tr key={index}>
                        <td>{movie.titleKorean}</td>
                        <td>{movie.titleEnglish}</td>
                        <td>{movie.productionYear}</td>
                        <td>{movie.productionCountry}</td>
                        <td>{movie.type}</td>
                        <td>{movie.genre}</td>
                        <td>{movie.productionStatus}</td>
                        <td>{movie.director}</td>
                        <td>{movie.productionCompany}</td>
                      </tr>
                  ))}
                  </tbody>
                </table>
            ) : (
                <p>검색 결과가 없습니다.</p>
            )}
          </div>
        </header>
      </div>
  );
}

export default App;
