import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [startYear, setStartYear] = useState('');
  const [endYear, setEndYear] = useState('');
  const [director, setDirector] = useState('');
  const [releaseStartDate, setReleaseStartDate] = useState(null);
  const [releaseEndDate, setReleaseEndDate] = useState(null);
  const [sortOption, setSortOption] = useState('latestUpdate');
  const [results, setResults] = useState([]);

  const handleSearch = (e) => {
    e.preventDefault();
    // KOBIS API 호출 로직 추가
    // 예제 데이터로 대체
    let fetchedResults = [
      { title: 'Movie 1', releaseDate: '2023-01-01', director: 'Director 1', updateDate: '2024-01-10', productionYear: '2022' },
      { title: 'Movie 2', releaseDate: '2023-02-01', director: 'Director 2', updateDate: '2024-01-05', productionYear: '2021' },
      { title: 'Movie 3', releaseDate: '2023-03-01', director: 'Director 3', updateDate: '2024-01-15', productionYear: '2022' }
    ];

    // 정렬 로직
    if (sortOption === 'latestUpdate') {
      fetchedResults.sort((a, b) => new Date(b.updateDate) - new Date(a.updateDate));
    } else if (sortOption === 'productionYear') {
      fetchedResults.sort((a, b) => b.productionYear - a.productionYear);
    } else if (sortOption === 'title') {
      fetchedResults.sort((a, b) => a.title.localeCompare(b.title));
    } else if (sortOption === 'releaseDate') {
      fetchedResults.sort((a, b) => new Date(a.releaseDate) - new Date(b.releaseDate));
    }

    setResults(fetchedResults);
  };

  return (
      <div className="App">
        <header className="App-header">
          <h1>KOBIS 영화 검색</h1>
          <form onSubmit={handleSearch}>
            <div className="form-row">
              <div style={{display: 'flex'}}>
                <div>
                  <div style={{display: 'flex'}}>
                    <h3>영화명</h3>
                    <input
                        type="text"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        placeholder="영화 제목을 입력하세요"
                    />

                    <h3>감독명</h3>
                    <input
                        type="text"
                        value={director}
                        onChange={(e) => setDirector(e.target.value)}
                        placeholder="감독 이름을 입력하세요"
                    />
                  </div>
                </div>
                <div className="form-row">
                  <div style={{display: 'flex'}}>
                    <h3>제작 연도</h3>
                    <input
                        type="number"
                        value={startYear}
                        onChange={(e) => setStartYear(e.target.value)}
                        placeholder="시작 연도"
                    />
                    <input
                        type="number"
                        value={endYear}
                        onChange={(e) => setEndYear(e.target.value)}
                        placeholder="종료 연도"
                    />

                    <h3>정렬 기준</h3>
                    <select value={sortOption} onChange={(e) => setSortOption(e.target.value)}>
                      <option value="latestUpdate">최근 업데이트 순</option>
                      <option value="productionYear">제작 연도</option>
                      <option value="title">영화명</option>
                      <option value="releaseDate">개봉일 순</option>
                    </select>
                  </div>
                </div>
              </div>

              <button type="submit">검색</button>
              <button type="reset">초기화</button>
            </div>
          </form>
          <div className="results">
            {results.length > 0 ? (
                <ul>
                  {results.map((movie, index) => (
                      <React.Fragment key={index}>
                        {index > 0 && <hr/>}
                        <li>
                          <div style={{display: 'flex', justifyContent: 'space-between'}}>
                            <h2>{movie.title}</h2>
                            <p>{movie.director}</p>
                          </div>
                          <p>제작 연도: {movie.productionYear}</p>
                          <p>개봉일: {movie.releaseDate}</p>
                          <p>업데이트 날짜: {movie.updateDate}</p>
                        </li>
                      </React.Fragment>
                  ))}
                </ul>
            ) : (
                <p>검색 결과가 없습니다.</p>
            )}
          </div>
        </header>
      </div>
  );
}

export default App;
