import React, { useState } from 'react';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = (e) => {
    e.preventDefault();
    // 여기에 KOBIS API 호출 로직을 추가하세요
    // 예제 데이터로 대체
    setResults([
      { title: 'Movie 1', releaseDate: '2023-01-01', director: 'Director 1' },
      { title: 'Movie 2', releaseDate: '2023-02-01', director: 'Director 2' },
    ]);
  };

  return (
      <div className="App">
        <header className="App-header">
          <h1>KOBIS 영화 검색</h1>
          <form onSubmit={handleSearch}>
            <input
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="영화 제목을 입력하세요"
            />
            <button type="submit">검색</button>
          </form>
          <div className="results">
            {results.length > 0 ? (
                <ul>
                  {results.map((movie, index) => (
                      <li key={index}>
                        <h2>{movie.title}</h2>
                        <p>개봉일: {movie.releaseDate}</p>
                        <p>감독: {movie.director}</p>
                      </li>
                  ))}
                </ul>
            ) : (
                <p>검색 결과가 없습니다.</p>
            )}
          </div>
        </header>
      </div>
  );
}

export default App;
